Properties.Formula:
"<div style='font-family: Helvetica, Arial, sans-serif;'>" & 

  "<div style='position: relative; width: 100%; max-width: 300px; height: 18px; background-color: #e0e0e0; border-radius: 10px; overflow: hidden; display: flex; position: relative;'>" &

    // Green section (up to 100%), but width will be 0% if [% Complete (Hours)] is 0 or NULL
    "<div style='height: 18px; background-color: #50C878; border-radius: 10px; width: " & 
      ToText(If(IsNull([% Complete (Hours)]) or [% Complete (Hours)] = 0, 0, Min([% Complete (Hours)] * 100, 100))) & "%;'></div>" &

    // Red section (only for the part beyond 100%)
    If([% Complete (Hours)] > 1, 
      "<div style='height: 18px; background-color: red; width: " & 
        ToText(Min(([% Complete (Hours)] * 100 - 100), 100)) & "%;'></div>", 
      ""
    ) &

    // Percentage text centered inside the bar, shows 0% if [% Complete (Hours)] is missing or zero
    "<div style='position: absolute; top: 0; left: 50%; transform: translate(-50%, 0); width: 100%; height: 18px; text-align: center; \n        line-height: 18px; font-weight: bold; color: white;'>" & 
      ToText(Round(If(IsNull([% Complete (Hours)]) or [% Complete (Hours)] = 0, 0, [% Complete (Hours)] * 100), 1)) & "%</div>" &

  "</div>" & 

  // Warning for exceeded budget
  If([% Complete (Hours)] > 1, 
    "<div style='color: red; font-size: 12px; font-weight: bold; margin-top: 5px;'>Current Hours Exceeded Budget!</div>", 
    ""
  ) &

  // Message when the hours field is missing or zero
  If(IsNull([% Complete (Hours)]) or [% Complete (Hours)] = 0, 
    "<div style='color: orange; font-size: 12px; font-weight: bold; margin-top: 5px;'>Need to add estimated hours</div>", 
    ""
  ) &

"</div>"

var number score = Round([Calculated % Complete] * 100, 1);
var text percentDisplay = ToText($score) & " %";

// Three-color transition
var text background = 
  If($score <= 50, "#DF2935",  // Red for 0-50%
  $score <= 79, "#F39237",  // Yellow for 51-79%
  "#8FC58B");  // Green for 80-100%

var text progressBar = 
  "<div style='position: relative; width: 100%; max-width: 300px; height: 18px; background-color: #e0e0e0; border-radius: 10px; overflow: hidden;'>" &
  "<div style='min-width: 15px; width: " & ToText(Min(Max($score, 5), 100)) & "%; height: 18px; background-color:" & $background & "; border-radius: 10px;'></div>" &
  "<div style='position: absolute; top: 0; left: 50%; transform: translate(-50%, 0); text-align: center; width: 100%; color: " & If($score > 10, "white", "#4E3D42") & "; line-height: 18px; font-weight: bold;'>" & $percentDisplay & "</div>" &
  "</div>";

//Formula Output
"<div style='width: 100%; max-width: 300px; height: 18px;'>" & $progressBar & "</div>"

If( [Daily Log Photos]<> "" ,
    "<img style='max-height:200px; max-width:200px' src='" & URLRoot() & "up/" & Dbid () & "/a/r" & [Record ID#] & "/e8/v0' />"
)
// https://helpv2.quickbase.com/hc/en-us/articles/4418302351764-Uploading-files
// e[#] = FID of file ttachment field
// v0 = latest revision of the file
